mapper.insert:
		
        	SELECT
        		NVL(MAX(BRD_ID)+1, 1) AS INSERTKEY
        	FROM 
        		PORTAL.PT_BOARD
    	
		INSERT INTO /* Admin.boardInsert */ 
			PORTAL.PT_BOARD(
				  BRD_ID
				, BRD_NM
				, BRD_DESC
				, BRD_TYPE
				, POST_TYPE_YN
				, POST_TAG_YN
				, POST_FILE_YN
				, POST_CMNT_YN
				, POST_CMNT_FILE_YN
				, POST_POPUP_YN
				, POST_SECRET_YN
				, POST_FIX_YN
				, DEL_YN
				, CRT_DT_TM
				, CRT_USR_ID
			)
			VALUES(
				  #{insertKey}
				, #{BRD_NM}
				, #{BRD_DESC}
				, #{BRD_TYPE}
				, #{POST_TYPE_YN}
				, #{POST_TAG_YN}
				, #{POST_FILE_YN}
				, #{POST_CMNT_YN}
				, #{POST_CMNT_FILE_YN}
				, #{POST_POPUP_YN}
				, #{POST_SECRET_YN}
				, #{POST_FIX_YN}
				, #{DEL_YN}
				, SYSDATE
				, #{userId}
			)

mapper.update:
UPDATE /* Admin.boardUpdate */ 
	PORTAL.PT_BOARD
SET
	  BRD_NM = #{BRD_NM}
	, BRD_DESC = #{BRD_DESC}
	, BRD_TYPE = #{BRD_TYPE}
	, BRD_CRT_AUTH = #{BRD_CRT_AUTH}
	, POST_TYPE_YN = #{POST_TYPE_YN}
	, POST_TAG_YN = #{POST_TAG_YN}
	, POST_FILE_YN = #{POST_FILE_YN}
	, POST_CMNT_YN = #{POST_CMNT_YN}
	, POST_CMNT_FILE_YN = #{POST_CMNT_FILE_YN}
	, POST_POPUP_YN = #{POST_POPUP_YN}
	, POST_SECRET_YN = #{POST_SECRET_YN}
	, POST_FIX_YN = #{POST_FIX_YN}
	, DEL_YN = #{DEL_YN}
	, MOD_DT_TM = SYSDATE
	, MOD_USR_ID = #{userId}
WHERE
	BRD_ID = #{BRD_ID}
