mapper.insert:
		
        	SELECT
        		NVL(MAX(post.POST_ID)+1, 1) AS INSERTKEY
        	FROM 
        		PORTAL.BOARD_POST post
        	WHERE 1=1
				AND post.BRD_ID = #{BRD_ID}
    	
    	INSERT INTO
    		PORTAL.BOARD_POST(
    			POST_ID
    		  , BRD_ID
    		  , POST_TITLE
    		  , POST_CONTENT
    		  , POST_VIEW_AUTH
    		  , POPUP_YN
    		  , POPUP_START_DT_TM
    		  , POPUP_END_DT_TM
    		  , SECRET_YN
    		  , FIX_YN
    		  , DEL_YN
    		  , CRT_DT_TM
    		  , CRT_USR_ID
    		)
    		VALUES(
    			#{insertKey}
    		  , #{BRD_ID}
    		  , #{POST_TITLE}
    		  , #{POST_CONTENT}
    		  , #{POST_VIEW_AUTH}
    		  , #{POPUP_YN}
    		  , #{POPUP_START_DT_TM}
		      , #{POPUP_END_DT_TM}
    		  , #{SECRET_YN}
    		  , #{FIX_YN}
    		  , 'N'
    		  , SYSDATE
    		  , #{userId}
    		)

		
			SELECT
				NVL(MAX(post_file.FILE_ID)+1, 1) AS INSERTKEY
			FROM
				PORTAL.BOARD_POST_FILE post_file
			WHERE 1=1
		
		INSERT INTO 
			PORTAL.BOARD_POST_FILE(
    			FILE_ID
    		  , POST_ID
    		  , FILE_TYPE
    		  , PARENT_CMNT_ID
    		  , SRV_FILE_PATH
    		  , SRV_FILE_NM
    		  , ORG_FILE_NM
    		  , FILE_EXT
    		  , FILE_SIZE
    		  , DEL_YN
    		  , CRT_DT_TM
    		  , CRT_USR_ID
    		)
    		VALUES(
    			#{insertFileKey}
    		  , #{POST_ID}
    		  , #{orgFileType}
    		  , #{PARENT_CMNT_ID} 
    		  , #{uploadFilePath}
    		  , #{newFileName}
    		  , #{orgFileName}
    		  , #{orgFileType}
    		  , #{fileSize}
    		  , 'N'
    		  , SYSDATE
    		  , #{userId}
    	)

mapper.update:
 
		
    	UPDATE PORTAL.BOARD_POST
    		SET
    		    BRD_ID = #{BRD_ID}
    		  , POST_TITLE = #{POST_TITLE}
    		  , POST_CONTENT = #{POST_CONTENT}
    		  , POST_VIEW_AUTH = #{POST_VIEW_AUTH}
    		  , POPUP_YN = #{POPUP_YN}
    		  , POPUP_START_DT_TM = #{POPUP_START_DT_TM}
    		  , POPUP_END_DT_TM = #{POPUP_END_DT_TM}
    		  , SECRET_YN = #{SECRET_YN}
    		  , FIX_YN = #{FIX_YN}
    		  , DEL_YN = 'N'
    		  , MOD_DT_TM = SYSDATE
    		  , MOD_USR_ID = #{userId}
    		WHERE
    			POST_ID = #{POST_ID}
