mapper.insert:
		
        	SELECT
        		NVL(MAX(post.POST_ID)+1, 1) AS INSERTKEY
        	FROM 
        		PORTAL.PT_BOARD_POST post
    	
    	INSERT INTO /* Board.boardPostInsert */ 
    		PORTAL.PT_BOARD_POST(
    			POST_ID
    		  , BRD_ID
    		  , POST_TITLE
    		  , POST_CONTENT
    		  , POST_VIEW_AUTH
    		  , POPUP_YN
    		  , POPUP_START_DT_TM
    		  , POPUP_END_DT_TM
    		  , SECRET_YN
    		  , FIX_YN
    		  , DEL_YN
    		  , CRT_DT_TM
    		  , CRT_USR_ID
    		)
    		VALUES(
    			#{insertKey}
    		  , #{BRD_ID}
    		  , #{POST_TITLE}
    		  , #{POST_CONTENT}
    		  , #{POST_VIEW_AUTH}
    		  , #{POPUP_YN}
    		  , #{POPUP_START_DT_TM}
		      , #{POPUP_END_DT_TM}
    		  , #{SECRET_YN}
    		  , #{FIX_YN}
    		  , 'N'
    		  , SYSDATE
    		  , #{userId}
    		)

mapper.update:

   	UPDATE /* Board.boardPostUpdate */
   		PORTAL.PT_BOARD_POST
   	SET
   		    BRD_ID = #{BRD_ID}
   		  , POST_TITLE = #{POST_TITLE}
   		  , POST_CONTENT = #{POST_CONTENT}
   		  , POST_VIEW_AUTH = #{POST_VIEW_AUTH}
   		  , POPUP_YN = #{POPUP_YN}
   		  , POPUP_START_DT_TM = #{POPUP_START_DT_TM}
   		  , POPUP_END_DT_TM = #{POPUP_END_DT_TM}
   		  , SECRET_YN = #{SECRET_YN}
   		  , FIX_YN = #{FIX_YN}
   		  , DEL_YN = 'N'
   		  , MOD_DT_TM = SYSDATE
   		  , MOD_USR_ID = #{userId}
   		WHERE 1=1
   			AND POST_ID = #{POST_ID}

 	UPDATE /* Board.boardPostDelete */
 		PORTAL.PT_BOARD_POST
 	SET
		    DEL_YN = 'Y'
		  , MOD_DT_TM = SYSDATE
		  , MOD_USR_ID = #{userId}
		WHERE 1=1
			AND POST_ID = #{POST_ID}
			AND BRD_ID = #{BRD_ID}

